from django.db import models
import pymysql

# Create your models here.
class Paper(models.Model):
    id = models.AutoField(primary_key=True)
    papertitle = models.TextField(null=True,blank=True)
    authorname = models.TextField(null=True,blank=True)
    paperabstract = models.TextField(null=True, blank=True)
    paperkeyword=models.TextField(null=True,blank=True)
    def __str__(self):
        return "Title: "+str(self.papertitle)+"--> Author: " +str(self.authorname)

class People(models.Model):
    id = models.AutoField(primary_key=True)
    name = models.CharField(max_length=50,null=True, blank=True)
    lname = models.CharField(max_length=100,null=True, blank=True)
    Kkywords = models.TextField(null=True, blank=True)
    papertitles = models.TextField(null=True,blank=True)
    degree = models.TextField(null=True,blank=True)
    def __str__(self):
        return "Name: "+str(self.name)+"--> Lname: " +str(self.lname)


